import { asNew, mark, intercept } from "../helpers/index.js";
export default status;

function status(promise, create) {
  const p = asNew(promise, create);
  if (mark.get(p, 'status')) return p;
  mark.set(p, 'status');
  p.status = 'pending';
  p.value = null;
  p.reason = null;
  return intercept(p, px => {
    return px.then(val => {
      p.status = 'resolved';
      p.value = val;
      return val;
    }).catch(err => {
      p.status = 'rejected';
      p.reason = err;
      return Promise.reject(err);
    });
  });
}